import React, { Component } from 'react';
import styled from "styled-components";
import {Link} from 'react-router-dom';
import { ProductConsumer } from "../context";


class Product extends Component {
    render() {
        const {id, title, img ,props, inCart} = this.props.product
        return (
            <ProductWrapper className="col-9 max auto col-md-6 col-lg-3 my-3">
                <div className="card">
                   <div className="img-container p-5" 
                   onClick={()=>console.log("you-clicked-me")}> 
                   <Link to="/details">
                   <img src={img} alt="product" className="card-img-top">
                    </img>
                    </Link>
                    <button className="cart-btn" disabled={inCart ? true:
                        false} onClick={()=>{console.log("Added to chart")}}
                        />
                        {inCart?(<p calssName="text-capitalize mb-0" disabled>in cart</p>):
                        (<i className="fas fa-cart-plus"></i>)}
                   </div>
                </div>
            </ProductWrapper>
        );
    }
}

const ProductWrapper = styled.div
`
` 

export default Product;
